var retrieveSchema = function(schemaName, cb) {
    pm.sendRequest({
        url: "https://raw.githubusercontent.com/folio-org/mod-inventory-storage/" + pm.variables.get("mod-inventory-storage_version") + "/ramls/" + schemaName,
        method: 'GET',
    }, function(err, res) {
        if (err !== null) {
            console.log("Schema retrieval error: " + err);
            cb("failed", null);
        } else {
            if (res.code === 200) {
                cb(null, res.json());
            } else {
                console.log("Schema retrieval falied: " + res.reason());
            cb("failed", null);
            }
        }
    });
};

