var retrieveSchema = function(module, version, schemaLoc, schemaName, cb) {
    pm.sendRequest({
        url: "https://raw.githubusercontent.com/folio-org/" + module + "/" + version + schemaLoc + schemaName,
        method: 'GET',
    }, function(err, res) {
        if (err !== null) {
            console.log("Schema retrieval error for: " + schemaName + " - " + err);
            cb("failed", null);
        } else {
            if (res.code === 200) {
                cb(null, res.json());
            } else {
                console.log("Schema retrieval falied for: " + schemaName + " - "  + res.reason());
            cb("failed", null);
            }
        }
    });
};

var retrieveModuleSchema = (schemaName, cb) => {
	const module = pm.variables.get("module");
	const version = pm.variables.get("module_version");
	retrieveSchema(module, version, "/ramls/", schemaName, cb);
};

var retrieveRAMLUtilsSchema = function(schemaName, cb) {
	const version = pm.variables.get("raml-utils_version");
	retrieveSchema("raml", version, "/schemas/", schemaName, cb);
};

var login = function() {
	//Clear out the previously set token as it may be expired or invalid
	//by the time this test runs again, since it is stored in an
	//environment variable.
	let mod = pm.variables.get("module");

	//Acquire a valid auth token
	if (pm.variables.has("xokapitoken-" + mod) === false) {
	 pm.sendRequest({
	     url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/authn/login",
	     method: 'POST',
	     header: {
	         'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
	         'Content-Type': 'application/json'
	     },
	     body: {
	         mode: 'raw',
	         raw : JSON.stringify({ username: pm.environment.get("username"), password: pm.environment.get("password")})
	     }
	 }, function(err, res) {
	     if (err !== null) {
	         console.log("Login error: " + err);
	     } else {
	         if (res.code === 201) {
	             pm.variables.set("xokapitoken-" + mod, res.headers.get("X-Okapi-Token"));
	         } else {
	             console.log("Login failed: " + res.reason());
	         }
	     }
	 });
	}
}

var createUser = function() {
	var uuid = require('uuid');

	pm.variables.set('test_user_id', uuid.v4());

	let token = pm.variables.get("xokapitoken-" + pm.variables.get("module"));

	pm.sendRequest({
	    url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/users",
	    method: 'POST',
	    header: {
	        'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
	        'X-Okapi-Token': token,
	        'Content-Type': 'application/json'
	    },
	    body: {
	        mode: 'raw',
	        raw : JSON.stringify({ username: 'test_user', id: pm.variables.get('test_user_id')})
	    }
	}, function(err, res) {
	    if (err !== null) {
	        console.log("user creation error: " + err);
	    } else {
	        if (res.code === 201) {
	            pm.sendRequest({
	                url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/authn/credentials",
	                method: 'POST',
	                header: {
	                    'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
	                    'X-Okapi-Token': token,
	                    'Content-Type': 'application/json'
	                },
	                body: {
	                    mode: 'raw',
	                    raw : JSON.stringify({ username: 'test_user', id: pm.variables.get('test_user_id'), password: 'test_user'})
	                }
	            }, function(err, res) {
	                if (err !== null) {
	                    console.log("Credential creation error: " + err);
	                } else {
	                    if (res.code === 201) {
	                        pm.variables.set('test_user_credId', res.json().id);
	                        pm.sendRequest({
	                            url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/perms/users",
	                            method: 'POST',
	                            header: {
	                                'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
	                                'X-Okapi-Token': token,
	                                'Content-Type': 'application/json'
	                            },
	                            body: {
	                                mode: 'raw',
	                                raw : JSON.stringify({ userId: pm.variables.get('test_user_id'), permissions: []})
	                            }
	                        }, function(err, res) {
	                            if (err !== null) {
	                                console.log("permissions creation error: " + err);
	                            } else {
	                                if (res.code === 201) {
	                                    pm.variables.set('test_user_permsId', res.json().id);
	                                    pm.sendRequest({
	                                        url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/authn/login",
	                                        method: 'POST',
	                                        header: {
	                                            'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
	                                            'Content-Type': 'application/json'
	                                        },
	                                        body: {
	                                            mode: 'raw',
	                                            raw : JSON.stringify({ username: 'test_user', password: 'test_user'})
	                                        }
	                                    }, function(err, res) {
	                                        if (err !== null) {
	                                            console.log("Test user login error: " + err);
	                                        } else {
	                                            if (res.code === 201) {
	                                                pm.variables.set('test_user_token', res.headers.get("X-Okapi-Token"));
	                                            } else {
	                                                console.log("Test user login failed: " + res.reason());
	                                            }
	                                        }
	                                    });
	                                } else {
	                                    console.log("permissions creation failed: " + res.reason());
	                                }
	                            }
	                        });
	                    } else {
	                        console.log("Auth creation failed: " + res.reason());
	                    }
	                }
	            });
	        } else {
	            console.log("User creation failed: " + res.reason());
	        }
	    }
	});
};

var cleanUpUserTest = function() {
    eval(pm.variables.get("cleanUpVariablesFunction"));

    let token = pm.variables.get("xokapitoken-" + pm.variables.get("module"));

    pm.sendRequest({
        url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/users/" + pm.variables.get('test_user_id'),
        method: 'DELETE',
        header: {
            'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
            'X-Okapi-Token': token,
            'Content-Type': 'application/json'
        }
    }, function(err, res) {
        pm.test("user deleted", function() {
            pm.expect(res).to.have.status(204);
            cleanUpVariables(["test_user_id", "test_user_token"]);

            pm.sendRequest({
                url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/authn/credentials/" + pm.variables.get('test_user_credId'),
                method: 'DELETE',
                header: {
                    'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
                    'X-Okapi-Token': token,
                    'Content-Type': 'application/json'
                }
            }, function(err, res) {
                pm.test("credentials deleted", function() {
                    pm.expect(res).to.have.status(204);
                    cleanUpVariables(["test_user_credId"]);

                    pm.sendRequest({
                        url: pm.environment.get("protocol") + "://" + pm.environment.get("url") + ":" + pm.environment.get("okapiport") + "/perms/users/" + pm.variables.get('test_user_permsId'),
                        method: 'DELETE',
                        header: {
                            'X-Okapi-Tenant': pm.environment.get("xokapitenant"),
                            'X-Okapi-Token': token,
                            'Content-Type': 'application/json'
                        }
                    }, function(err, res) {
                        pm.test("permissions deleted", function() {
                            pm.expect(res).to.have.status(204);
                            cleanUpVariables(["test_user_permsId"]);
                        });

                    });
                });
            });
        });
    });
};

var cleanUpVariables = function(vars) {
	for (let i = 0; i < vars.length; i++) {
		pm.variables.unset(vars[i]);
		pm.expect(pm.variables.get(vars[i]), vars[i] + " exists").to.not.exist;
	}
}

